import React from 'react';
import qrImage from 'qr-image';

export default function Avatar({userid}) {
    const qrImageData = qrImage.imageSync(qrCodeUrl, { type: 'png', size: 3 });

    const qrCodeImageUrl = `data:image/png;base64,${qrImageData.toString('base64')}`;

    return (
        <div>
            <img src={qrCodeImageUrl} />
        </div>
    );
}
